mapper.insert:
insert into p_city(cityId
	, country
	, lordId
	, endTime
	, protectedTime
	, isDestroyed
	, lastSaveTime
	, electionData
	, electionEndTime
) values (#{cityId}
	, #{country}
	, #{lordId}
	, #{endTime}
	, #{protectedTime}
	, #{isDestroyed}
	, #{lastSaveTime}
	, #{electionData}
	, #{electionEndTime}
)

mapper.update:
update p_city 
set country = #{country}
	, lordId = #{lordId}
	, endTime = #{endTime}
	, protectedTime = #{protectedTime}
	, isDestroyed = #{isDestroyed}
	, lastSaveTime = #{lastSaveTime}
	, electionData = #{electionData}
	, electionEndTime = #{electionEndTime}
	, monsterData = #{monsterData}
	, sendAwardTime = #{sendAwardTime}
	, cityLv = #{cityLv}
	, makeItemTime = #{makeItemTime}
	, awardNum = #{awardNum}
	, warAttender = #{warAttender}
	, people = #{people}
	, nextAttackTime = #{nextAttackTime}
	, state = #{state}
	, breakTime = #{breakTime}
	, cityTime = #{cityTime}
	, flush = #{flush}
	, exp = #{exp}
	, cityName = #{cityName}
where cityId = #{cityId}
