#{gold}
, goldCost = #{goldCost}
, goldGive = #{goldGive}
where lordId = #{lordId}

update p_lord set ingot =
#{ingot}
, vip = #{vip}
where lordId = #{lordId}

update p_lord set exp =
#{exp}
, level = #{level}
, lvUpTime = #{lvUpTime}
where lordId = #{lordId}

update p_lord set equip = #{equip}
, gold = #{gold}
, goldCost = #{goldCost}
where lordId = #{lordId}
